function remove(event){
	let parent = event.target.parentElement.parentElement.getElementsByClassName("divTask");
	let child = event.target.parentElement;
	let index =Array.from(parent).indexOf(child);
	let day = event.target.parentElement.parentElement.firstChild;
	if(day.innerText=="Monday:"){
		mn.splice(index,1);
		localStorage.setItem("first",JSON.stringify(mn));
		child.remove();
	}else if(day.innerText=="Tuesday:"){
		tue.splice(index,1);
		localStorage.setItem("second",JSON.stringify(tue));
		child.remove();
	}else if(day.innerText=="Wednesday:"){
		wed.splice(index,1);
		localStorage.setItem("third",JSON.stringify(wed));
		child.remove();
	}else if(day.innerText=="Thursday:"){
		thursday.splice(index,1);
		localStorage.setItem("fourth",JSON.stringify(thursday));
		child.remove();
	}
}

function crossTask(event){
	let cross = event.target;
	let parent = cross.parentElement.parentElement.getElementsByClassName("divTask");
	let index =Array.from(parent).indexOf(cross.parentElement);
    let differ = cross.parentElement.parentElement.firstChild;
	if(cross.classList.contains("newTask")){
		cross.classList.remove("newTask");
		cross.classList.add("oldTask");
        if(differ.innerText=="Monday:"){
        	mn[index].isCrossed = true;
        	localStorage.setItem("first",JSON.stringify(mn));
        }else if(differ.innerText=="Tuesday:"){
        	tue[index].isCrossed = true;
        	localStorage.setItem("second",JSON.stringify(tue));
        }else if(differ.innerText=="Wednesday:"){
        	wed[index].isCrossed = true;
        	localStorage.setItem("third",JSON.stringify(wed));
        }else if(differ.innerText=="Thursday:"){
        	thursday[index].isCrossed = true;
        	localStorage.setItem("fourth",JSON.stringify(thursday));
        }	
	}else if (cross.classList.contains("oldTask")){
		cross.classList.remove("oldTask");
		cross.classList.add("newTask");
		if(differ.innerText=="Monday:"){
        	mn[index].isCrossed = false;
        	localStorage.setItem("first",JSON.stringify(mn));
        }else if(differ.innerText=="Tuesday:"){
        	tue[index].isCrossed = false;
        	localStorage.setItem("second",JSON.stringify(tue));
        }else if(differ.innerText=="Wednesday:"){
        	wed[index].isCrossed = false;
        	localStorage.setItem("third",JSON.stringify(wed));
        }else if(differ.innerText=="Thursday:"){
        	thursday[index].isCrossed = false;
            localStorage.setItem("fourth",JSON.stringify(thursday));
        }
	}
}
